<script setup>
import { reactive } from 'vue';

  const nome = "Leonardo Santos"
  const meuObj = {
    nome: "Leonardo",
    jogo: "Starfield"
  }

  const imgBatman = "data:image/png;base64,";
  const imagemSuperman = "data:image/jpeg;base64,"

  function dizOla(nome) {
    return `${nome} diz oi`
  }

  const botaDesaabilitado = false

  const gostaDoBatman = true
  const gostaDoSuperman = true

  const estaAutorizado = true

  //let contador = 0;

  const estado = reactive({
    contador: 0,
    email: '',
    saldo: 5000,
    transferindo: 0,
    nomes: ['leonardo', 'gian', 'paulo', 'soraya', 'simone'],
    nomeAInserir: '',
  })

  function incrementar() {
    estado.contador++;
  }

  function decrementar() {
    if(estado.contador > 0) {
      estado.contador--
    }
}
  
  function alteraEmail(evento) {
    estado.email = evento.target.value
  }

  function mostraSaldoFuturo() {
    const {saldo,transferindo} = estado

    return saldo - transferindo
  }

  function validaValorTransferencia() {
      const {saldo, transferindo } = estado;

      return saldo >= transferindo
    }

    function cadastraNome() {
      const {nomes, nomeAInserir} = estado

      if(nomeAInserir.length >= 3) {
        nomes.push(nomeAInserir)
      } else {
        alert('Digite mais caracteres')
      }



    }
    
</script>

<template>
  <h1>{{ dizOla("Hagaka") }}</h1>

  <img v-if="gostaDoBatman" v-bind:src="imgBatman" alt="">
  <img v-else-if="gostaDoSuperman" :src="imagemSuperman" alt="">
  <h2 v-else>Não curte heróis da DC</h2>
  
  <h1 v-if="estaAutorizado">Bem-Vindo</h1>
  <h1 v-else>Não possui acesso</h1>

  <button :disabled="!botaHabilitado">Enviar mensagem</button>
  <br>
  <hr>

  {{ estado.contador }}

  <button @click="incrementar" type="button">+</button>
  <button @click="decrementar" type="button">-</button>

  <br><hr>

  {{ estado.email }}
  <br>
  <input type="email" @keyup="alteraEmail">

  <br><br><hr><br><br>
  Saldo: {{ estado.saldo }}<br>
  transferindo: {{ estado.transferindo }}<br>
  Saldo depois da transferência {{ mostraSaldoFuturo() }}<br>
  <input class="campo" :class="{ invalido: !validaValorTransferencia() }" @keyup="evento => estado.transferindo = evento.target.value" type="number" placeholder="quantia para transferir">
  <button v-if="validaValorTransferencia()">transferir</button>
  <span v-else>Valor maior que o saldo</span>

  <br><hr>

  <ul>
    <li v-for="nome in estado.nomes">
      {{ nome }}
    </li>
  </ul>

  <input @keyup="evento => estado.nomeAInserir = evento.target.value" type="text" placeholder="Digite um novo nome">
  <button @click="cadastraNome()" type="button">Cadastrar</button>

  <h3 v-for="nome in estado.nomes">{{ nome }}</h3>
</template>

<style scoped>
  img {
    max-width: 200px;
  }

  .invalido {
    outline-color: red;
    border-color: red;
  }

  .campo{
    border: 2px solid #000
  }
</style>
